// @flow
import * as React from "react";
import { pagingCountries } from "./CountryService";
import { makeObservable, observable, action,runInAction } from "mobx";

class CountryStore {
  countryList = [];
  constructor() {
    makeObservable(this, {
      countryList: observable,
      post: action.bound,
      put: action.bound,
      delete: action.bound,
      getData: action.bound,
    });
  }
  async post(model) {}
  async put(model) {}
  async delete(id) {}

  async getData(searchObject) {
    try {
      let data = await pagingCountries(searchObject);
      runInAction(() => {
        this.countryList = data.data;
      });
    } catch (error) {
      console.error(`error:${error}`);
    }
  }
  componentDidMount() {
    this.getData({ pageIndex: 1, pageSize: 10, keyword: "" });
  }
}
export default CountryStore;
