export const CREATE_COUNTRY = "CREATE_COUNTRY";
export const EDIT_COUNTRY = "EDIT_COUNTRY";
export const DELETE_COUNTRY = "DELETE_COUNTRY";

export const CreateCountry = (data) => (dispatch) => {
  dispatch({
    type: CREATE_COUNTRY,
    payload: data,
  });
};

export const EditCountry = (data) => (dispatch) => {
  dispatch({
    type: EDIT_COUNTRY,
    data: data,
  });
};

export const DeleteCountry = (data) => (dispatch) => {
  dispatch({
    type: DELETE_COUNTRY,
    data: data,
  });
};
