import { useEffect } from "react";
import { pagingCountries } from "./CountryService";
import { editCountry } from "./CountryService";
import { deleteCountry } from "./CountryService";

import { useState } from "react";
import * as React from "react";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Button from "@mui/material/Button";
import { EditCountry } from "app/redux/actions/CountryAction";
import { DeleteCountry } from "app/redux/actions/CountryAction";
import FormAdd from "./FormAdd";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

export default function CountryIndex() {
  const [countrys, setCountry] = useState([]);
  const [showAdd,setShowAdd]=useState(false)
  useEffect(() => {
    loadCountries();
    loadCountries().then((data) => {
      setCountry(data.data.content);
    });
  }, []);

  async function loadCountries() {
    let searchObject = {
      pageIndex: 1,
      pageSize: 20,
    };
    let data = await pagingCountries(searchObject);
    console.log(data.data.content);
    return data;
  }

  
  //////////////////////////Edit
  useEffect(() => {
    EditCountries();
  }, []);
  async function EditCountries() {
    let data = await EditCountry();

    console.log(data);
  }
  //////////////////////////Delete
  useEffect(() => {
    DeleteCountries();
  }, []);
  async function DeleteCountries() {
    let data = await DeleteCountry();

    console.log(data);
  }

  /* 
  const newobj = {
      name: "Ha Lan",
      code: "HL",
      description: "Quoc gia Ha Lan",
    };
    dispatch(CreateCountry1(newobj)); */
  function handleShowAdd() {
    setShowAdd(!showAdd);

  }
  return (
    <>
      {showAdd && (
        <FormAdd
          exit={setShowAdd}
          loadCountries={loadCountries}
          setCountry={setCountry}
        />
      )}
      <TableContainer
        component={Paper}
        sx={{ mx: "auto", maxWidth: 650, mt: "30px", mb: "30px" }}
      >
        <Button onClick={handleShowAdd} variant="contained" sx={{}}>
          Thêm
        </Button>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow sx={{ bgcolor: "info.main" }}>
              <StyledTableCell align="center">STT</StyledTableCell>
              <StyledTableCell align="left">Name</StyledTableCell>
              <StyledTableCell align="left">Code</StyledTableCell>
              <StyledTableCell align="left">Description</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {countrys.map((row, id) => (
              <StyledTableRow
                key={row.id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <StyledTableCell align="center">{id}</StyledTableCell>
                <StyledTableCell align="left">{row.name}</StyledTableCell>
                <StyledTableCell align="left">{row.code}</StyledTableCell>
                <StyledTableCell align="left">
                  {row.description}
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
