import React, { useState,useEffect } from "react";
import { deleteCountry } from "../CountryService";
import "./styles.scss"
function FormDelete({ idDelete, setShowDelete, loadCountries,setCountry }) {
  const [idcountry, setIdCountry] = useState("");
  function hadleBackDelete() {
    setShowDelete(false);
  }
  function handleDelete() {
    setIdCountry(idDelete);
  }

  useEffect(() => {
    deletecountry();
  }, [idcountry]);

  
  return (
    <div className="row">
      <div className="col-md-12  box-background">
        <div className="box-confirm ">
          <div className="mes-warrming">
            <p>Bạn thực sự muốn xóa quốc gia này?</p>
          </div>
          <button
            className="btn btn-cache btn-danger "
            onClick={hadleBackDelete}
          >
            Hủy
          </button>
          <button
            className="btn btn-confirm btn-primary "
            onClick={() => {
              handleDelete();
            }}
          >
            Xác nhận
          </button>
        </div>
      </div>
    </div>
  );
}

export default FormDelete;
