import React from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import "./styles.scc";
function FormAdd({ exit, data, setCountry }) {
  /////
  const formik = useFormik({
    initialValues: {
      name: "",
      code: "",
      description: "",
    },
    validationSchema: Yup.object({
      name: Yup.string()
        .min(5, "Tên quốc gia quá ngắn!")
        .max(15, "Tên quốc gia quá Dài!")
        .required("Bắt buộc"),
      code: Yup.string()
        .min(1, "code quá ngắn!")
        .max(10, "code quá dài!")
        .required("Bắt buộc"),
      description: Yup.string()
        .min(10, "description quá ngắn!")
        .max(30, "description quá dài!")
        .required("Bắt buộc"),
    }),
    onSubmit: (values) => {
      handleAddCountry(data, values);
    },
  });
  ////

  function handleAddCountry(data, value) {
    let newData = [...data];
    newData = [...newData, value];
    setCountry(newData);
    exit(false);
  }
  function handleExit() {
    exit(false);
  }
  return (
    <>
      <div className="row">
        <div className="col-md-12 box-background ">
          <div className="form-add ">
            <form onSubmit={formik.handleSubmit}>
              <div className="box-input">
                <label htmlFor="name">Name</label>
                <input
                  autoComplete="off"
                  id="name"
                  name="name"
                  type="text"
                  onChange={formik.handleChange}
                  onBlur={formik.handleBlur}
                  value={formik.values.name}
                />
                {formik.touched.name && formik.errors.name ? (
                  <div className="warrming">{formik.errors.name}</div>
                ) : null}
              </div>
              <div className="box-input">
                <label htmlFor="code">Code</label>
                <input
                  autoComplete="off"
                  id="code"
                  name="code"
                  type="text"
                  onChange={formik.handleChange}
                  onBlur={formik.handleBlur}
                  value={formik.values.code}
                />
                {formik.touched.code && formik.errors.code ? (
                  <div className="warrming">{formik.errors.code}</div>
                ) : null}
              </div>

              <div className="box-input">
                <label htmlFor="description">Description</label>
                <input
                  autoComplete="off"
                  id="description"
                  name="description"
                  type="text"
                  onChange={formik.handleChange}
                  onBlur={formik.handleBlur}
                  value={formik.values.description}
                />
                {formik.touched.description && formik.errors.description ? (
                  <div className="warrming">{formik.errors.description}</div>
                ) : null}
              </div>
              <div>
                <button
                  className="btn btn-danger pr-4 mr-3"
                  onClick={handleExit}
                >
                  Thoát
                </button>
                <button className="btn btn-primary pr-4 mr-3" type="submit">
                  Thêm
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default FormAdd;
